   /**
    * Reset password with token
    */
   public async resetPassword(
     resetToken: string,
     newPassword: string
   ): Promise<{ success: boolean; message?: string; error?: string }> {
     try {
       // Get token data from Redis
       const tokenDataStr = await this.redis.get(`password_reset:${resetToken}`);
       if (!tokenDataStr) {
         return { success: false, error: 'Invalid or expired reset token' };
       }

       const tokenData = JSON.parse(tokenDataStr);
       const { userId } = tokenData;

       // Get user
       const user = await DatabaseService.client.user.findUnique({
         where: { id: userId },
         select: { id: true, email: true },
       });

       if (!user) {
         return { success: false, error: 'User not found' };
       }

       // Validate new password
       const passwordValidation = this.validatePassword(newPassword);
       if (!passwordValidation.valid) {
         return { success: false, error: passwordValidation.message };
       }

       // Hash new password
       const newPasswordHash = await this.hashPassword(newPassword);

       // Update password and reset login attempts
       await DatabaseService.client.user.update({
         where: { id: userId },
         data: {
           passwordHash: newPasswordHash,
           loginAttempts: 0,
           lockedUntil: null,
         },
       });

       // Delete reset token
       await this.redis.del(`password_reset:${resetToken}`);

       // Revoke all user sessions
       await this.logoutAll(userId);

       logger.info('Password reset successful', { userId });

       return { success: true, message: 'Password reset successfully' };
     } catch (error: unknown) {
       logger.error('Password reset failed', undefined, {
         errorMessage: error instanceof Error ? error.message : String(error),
       });
       return { success: false, error: 'Failed to reset password' };
     }
   }

  /**
   * Register new user
   */
  public async register(userData: {
    email: string;
    password: string;
    firstName?: string;
    lastName?: string;
    phone?: string;
    role?: string;
    schoolId?: string;
  }): Promise<{ success: boolean; user?: any; error?: string }> {
    try {
      // Validate password
      const passwordValidation = this.validatePassword(userData.password);
      if (!passwordValidation.valid) {
        return { success: false, error: passwordValidation.message };
      }

      // Check if user already exists
      const existingUser = await DatabaseService.client.user.findUnique({
        where: { email: userData.email.toLowerCase() },
      });

      if (existingUser) {
        return { success: false, error: 'User with this email already exists' };
      }

      // Hash password
      const passwordHash = await this.hashPassword(userData.password);

      // Create user
      const user = await DatabaseService.client.user.create({
        data: {
          email: userData.email.toLowerCase(),
          passwordHash,
          firstName: userData.firstName,
          lastName: userData.lastName,
          phone: userData.phone,
          role: userData.role || 'parent',
          schoolId: userData.schoolId,
        },
      });

      return {
        success: true,
        user: {
          id: user.id,
          email: user.email,
          firstName: user.firstName,
          lastName: user.lastName,
          role: user.role,
        },
      };
    } catch (error: unknown) {
      logger.error('User registration failed', undefined, {
        errorMessage: error instanceof Error ? error.message : String(error),
      });
      return { success: false, error: 'Failed to register user' };
    }
  }

